import React, { Component } from 'react';

class Chatbox extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            messages: props.messages,
            messagesComponent: null,
        }
    }

    componentDidUpdate = () =>{
        this.state.messagesComponent = this.mapMesssageArrayToComponents();
        console.log(this.state.messages);
        console.log(this.state.messagesComponent);
    }
    
    mapMesssageArrayToComponents = () =>
        this.state.messages.map(message =>  <p>{message}</p>);


    render() { 
        return ( 
            <div id='Chatbox Container'>
                <div>{this.mapMesssageArrayToComponents()}</div>
            </div>
         );
    }
}
 
export default Chatbox;
